import Card from "../UI/Card.js"; //.. for uplevel and then follows the path UI/Card.js
import ExpenseItem from "./ExpenseItem";
import "./Expense.css";
import ExpensesFilter from "./ExpenseFilter.js";
import React, { useState } from "react";

function Expense(props) {
  const [filteredYear, setYear] = useState("2019");
  const filterChangeHandler = (year) => {
    setYear(year);
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />
        {props.items.map((expense) =>
          props.items.filter((year) => {
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date.year}
            ></ExpenseItem>;
          })
        )}
      </Card>
    </div>
  );
}
export default Expense;
