import ChartBar from './ChartBar.js';
import './Chart.css';

const Chart=(props)=>{
    const dataPointValues=props.dataPoint.map((eachDataPoint)=>(eachDataPoint.value));
    //New array that contains all values of dataPoints.(numbers)
    const totalMax=Math.max(...dataPointValues);
    //To find max value on the array dataPointValue
return(
    <div className='chart'>
    {props.dataPoints.map(dataPoint =><ChartBar key={dataPoint.id} 
    key={props.id}
    value={props.value} 
    maxValue={totalMax}
    label={props.label}></ChartBar>
    )}
    </div>
)
}
export default Chart;
