import Card from "../UI/Card.js"; //.. for uplevel and then follows the path UI/Card.js
import ExpenseItem from "./ExpenseItem";
import './Expense.css';
import ExpensesFilter from "./ExpenseFilter.js";
import React,{ useState } from "react";

function Expense(props) {
  const[year, setYear]=useState(props.items.date.getFullYear());
  const yearFilter=(option)=>{
    setYear(option);
  }
  return (
    <div>
      <Card className='expenses'>
      <ExpensesFilter onChange={yearFilter}/>
      <ExpenseItem
        title={props.items[0].title}
        amount={props.items[0].amount}
        date={props.items[0].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[1].title}
        amount={props.items[1].amount}
        date={props.items[1].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[2].title}
        amount={props.items[2].amount}
        date={props.items[2].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[3].title}
        amount={props.items[3].amount}
        date={props.items[3].date}
      ></ExpenseItem>
    </Card>
    </div>
  );
}
export default Expense;
